package com.tp.memberUpdate;

public class MemberUdate {
	private String r_name;
	private String r_pw;  
	private String r_number;
	private String r_email;
	private String r_id2;

public MemberUdate() {
	// TODO Auto-generated constructor stub
}

public MemberUdate(String r_name, String r_pw, String r_number, String r_email, String r_id2) {
	super();
	this.r_name = r_name;
	this.r_pw = r_pw;
	this.r_number = r_number;
	this.r_email = r_email;
	this.r_id2 = r_id2;
}

public String getR_name() {
	return r_name;
}

public void setR_name(String r_name) {
	this.r_name = r_name;
}

public String getR_pw() {
	return r_pw;
}

public void setR_pw(String r_pw) {
	this.r_pw = r_pw;
}

public String getR_number() {
	return r_number;
}

public void setR_number(String r_number) {
	this.r_number = r_number;
}

public String getR_email() {
	return r_email;
}

public void setR_email(String r_email) {
	this.r_email = r_email;
}

public String getR_id2() {
	return r_id2;
}

public void setR_id2(String r_id2) {
	this.r_id2 = r_id2;
}





}
